import React, {useState} from 'react'
import {makeStyles, withStyles} from "@material-ui/core/styles";
import {TextField, Typography, Button, Grid, Box} from "@material-ui/core"
import SendIcon from "@material-ui/icons/Send";
import Navbar from './Navbar'

const useStyles = makeStyles(theme=> ({
    form: {
        top: "50%",
        left: "50%",
        transform: "translate(-50%, -50%)",
        position: "absolute"
    },
    button: {
        marginTop: "1rem",
        color: "tomato",
        borderColor: "tomato"
    }
}));

const InputField = withStyles({
    root: {
        "& label.Mui-focused": {
            color: "tomato",
        },
        "& label": {
            color: "tan"
        },
        "& .MuiOutlinedInput-root":{
            "& fieldset": {
                borderColor: "tan"
            },
            "&:hover fieldset":{
                borderColor: "tan"
            },
            "&.Mui-focused fieldset":{
                borderColor: "tan"
            }
        }
    },
})(TextField);


const handleSubmit = function(name, email, company) {
    alert("✔️ This works on every component! "+name+" "+email+" "+company);
    
}

const Contact = () => {
    
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [company, setCompany] = useState('');

    const classes = useStyles()
    
    
    return (
        <Box component="div">
            <Navbar />
            <Grid container justify="center">
                <Box component="form" className={classes.form}>
                    <Typography variant="h5" style={{color: "tomato", textAlign: "center", textTransform: "uppercase"}}>
                        Hire or Contact Me
                    </Typography>
                    <InputField fullWidth={true} label="Name" variant="outlined" inputProps={{ style: {color: "white" }}} margin="dense" size="medium"
                    value={name} 
                    onChange={(e) => {setName(e.target.value)}}/>
                    <br/>
                    <InputField fullWidth={true} label="Email" variant="outlined" inputProps={{ style: {color: "white" }}} margin="dense" size="medium"
                    value={email} 
                    onChange={(e) => {setEmail(e.target.value)}}/>
                    <br/>
                    <InputField fullWidth={true} label="Company" variant="outlined" inputProps={{ style: {color: "white" }}} margin="dense" size="medium"
                    value={company} 
                    onChange={(e) => {setCompany(e.target.value)}}/>
                    <br/>
                    <Button className={classes.button} variant="outlined" fullWidth={true} endIcon={<SendIcon />} onClick={() => {handleSubmit(name,email,company); setName('')}}>
                        Contact Me
                    </Button>
                </Box>
            </Grid>
        </Box>
    )
}

export default Contact
